import React, { useEffect } from "react";
import styled, { css } from "styled-components";

type StateProps = {
    readonly state?: string;
};

const Main = styled.main<StateProps>`
    position: absolute;
    top: 0;
    width: 100%;
    max-height: 100%;
    grid-column-start: 2;
    display: grid;
    overflow-y: auto;

    ${(props) =>
        props.state === "entered"
            ? css`
                  visibility: visible;
                  opacity: 1;
                  transform: scale(1);
                  transition: 0.3s ease-out;
              `
            : props.state === "entering"
            ? css`
                  visibility: hidden;
                  opacity: 0;
                  transform: scale(0.9);
                  transition: 0.3s ease-in;
              `
            : css`
                  visibility: hidden;
                  opacity: 0;
                  transition: 0.15s ease-in;
              `};
`;

const Turnos = function ({ state }) {
    useEffect(() => {}, []);

    return (
        <>
            <Main state={state}></Main>
        </>
    );
};

export default Turnos;
