import React, { useEffect } from "react";
import styled, { css } from "styled-components";
import { Device } from "components/globalStyle";
import map from "./img/map.svg";

type StateProps = {
    readonly state?: string;
};

const Container = styled.div<StateProps>`
    display: grid;
    gap: 4px;

    @media ${Device.laptop} {
        position: absolute;
        top: 0;
        right: 0;
        width: 60vw;
        height: 100vh;
        overflow-y: auto;
    }

    ${(props) =>
        props.state === "entering"
            ? css`
                  visibility: hidden;
                  opacity: 0;
                  transform: scale(1.1);
                  transition: 0.15s ease-in;
              `
            : props.state === "exiting"
            ? css`
                  visibility: hidden;
                  opacity: 0;
                  transform: scale(0.9);
                  transition: 0.15s ease-in;
              `
            : css`
                  visibility: visible;
                  opacity: 1;
                  transform: initial;
                  transition: 0.3s ease-out;
              `};
`;

const Img = styled.img`
    height: 100%;
`;

const Mapa = function ({ state }) {
    useEffect(() => {}, []);

    return (
        <>
            <Container state={state}>
                <Img src={map} alt="" />
            </Container>
        </>
    );
};

export default Mapa;
