import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { TransitionGroup, Transition } from "react-transition-group";
import styled, { css, ThemeProvider } from "styled-components";
import { themeDark, themeLight } from "themes";
import GlobalStyle, { Device } from "components/globalStyle";
import { Button } from "components/components";
import Home from "views/Home/Home";
import Galeria from "views/Galeria/Galeria";
import map from "./map.jpg";

const Page = styled.div`
    position: fixed;
    z-index: 1;
    top: 0;
    left: 0;
    width: 100vw;
    height: calc(100vh - 72px);
    display: grid;
    align-items: center;
    overflow-y: auto;

    @media ${Device.laptop} {
        grid-template-columns: 1fr 1fr;
    }

    @media ${Device.desktop} {
    }
`;

type StateProps = {
    readonly state?: string;
};

const Overlay = styled.div<StateProps>`
    position: fixed;
    top: 0;
    z-index: 2;
    inset: 0;
    width: 100vw;
    height: 100vh;
    background: var(--primary);
    opacity: 0;
    visibility: hidden;
    transition: 0.3s ease-in-out;

    ${(props) =>
        props.state === "entered" &&
        css`
            opacity: 0.8;
            visibility: visible;
        `};
`;

export const Fab = styled.div<StateProps>`
    position: fixed;
    z-index: 3;
    bottom: 0;
    width: 100%;
    padding: 8px;
    height: 72px;
    background: var(--secondary);
    color: var(--on-secondary);
    clip-path: polygon(
        62% 0,
        82% 27%,
        100% 25%,
        100% 100%,
        0 100%,
        0 30%,
        20% 27%,
        40% 0,
        50% 3%
    );
    transition: 0.25s ease-in;
    text-align: center;

    ${(props) =>
        props.state === "entered" &&
        css`
            background-color: var(--secondary-variant);
            height: calc(80% + 68px);
            opacity: 0.8;
            transition: 0.3s ease-out;
            clip-path: polygon(
                61% 1%,
                81% 1%,
                100% 0,
                100% 100%,
                0 100%,
                0 3%,
                12% 3%,
                27% 2%,
                43% 1%
            );
        `};

    ${Button} {
        padding: 7px 32px;
        border-radius: 40px;
        font-size: 2.4rem;
        line-height: 3.2rem;
    }
`;

const Side = styled.aside<StateProps>`
    position: fixed;
    z-index: 4;
    height: 80%;
    width: 100%;
    display: flex;
    visibility: hidden;
    bottom: -100%;
    transition: 0.25s ease-in;
    background: var(--surface);
    padding-top: 20px;
    clip-path: polygon(
        63% 1%,
        81% 1%,
        100% 0,
        100% 100%,
        0 100%,
        0 4%,
        13% 4%,
        30% 3%,
        53% 1%
    );

    @media ${Device.laptop} {
    }

    ${(props) =>
        props.state === "entered" &&
        css`
            visibility: visible;
            bottom: 0;
            transition: 0.3s ease-out;
        `};
`;

const Snackbar = styled.div<StateProps>`
    position: fixed;
    z-index: 100;
    left: 0;
    bottom: 0;
    width: 100%;
    min-height: 45px;
    background: var(--on-background);
    color: var(--on-secondary);
    padding: 12px 16px;
    border-radius: 5px;
    box-shadow: 0px 2px 3px rgba(0, 0, 0, 0.3);
    font-size: 13px;
    font-size: 1.3rem;
    line-height: 24px;
    line-height: 2.4rem;
    letter-spacing: 0.1px;
    letter-spacing: 0.01rem;
    font-weight: 400;
    text-transform: inherit;
    visibility: hidden;
    transform: translateY(100%);
    transition: 0.25s ease-in;

    @media ${Device.laptop} {
        width: auto;
        min-width: 344px;
        margin: 28px;
    }

    ${(props) =>
        props.state === "entered" &&
        css`
            visibility: visible;
            transform: translateY(0%);
            transition: 0.3s ease-out;
        `};
`;

const App = function () {
    const nodeRef = React.useRef(null);
    const [sideOpen, setSideOpen] = useState(false);
    const [snackbar, setSnackbar] = useState("");
    const [darkTheme, setDarkTheme] = useState(false);

    useEffect(() => {
        setDarkTheme(false);
    }, []);

    useEffect(() => {
        if (snackbar !== "") {
            setTimeout(function () {
                setSnackbar("");
            }, 5500);
        }
    }, [snackbar]);

    return (
        <Router>
            <ThemeProvider theme={darkTheme ? themeDark : themeLight}>
                <GlobalStyle />
            </ThemeProvider>
            <>
                <Route
                    render={({ location }) => (
                        <Page>
                            <TransitionGroup component={null}>
                                <Transition key={location.key} timeout={300}>
                                    {(state) => (
                                        <Switch
                                            key={location.key}
                                            location={location}
                                        >
                                            <Route
                                                exact
                                                path="/"
                                                render={() => (
                                                    <>
                                                        <Home state={state} />
                                                        <Galeria
                                                            state={state}
                                                        />
                                                    </>
                                                )}
                                            />
                                            <Route
                                                path="/turnos"
                                                render={() => <div />}
                                            />
                                        </Switch>
                                    )}
                                </Transition>
                            </TransitionGroup>
                            <Transition
                                nodeRef={nodeRef}
                                in={sideOpen}
                                addEndListener={(nodeRef, done) => {
                                    nodeRef.addEventListener(
                                        "transitionend",
                                        done,
                                        false
                                    );
                                }}
                            >
                                {(state) => (
                                    <>
                                        <Overlay
                                            state={state}
                                            onClick={() => {
                                                setSideOpen(false);
                                            }}
                                        />
                                        <Fab
                                            state={state}
                                            onClick={() => {
                                                setSideOpen(!sideOpen);
                                            }}
                                        >
                                            <Button outline>
                                                Encuentranos
                                            </Button>
                                        </Fab>
                                        <Side state={state}>
                                            <p>
                                                Encuéntranos en primera línea de
                                                playa y a dos cuadras del centro
                                                de la ciudad • Donde estamos:
                                                muelle 1 Parador Cabo frio.
                                            </p>
                                            <img src={map} alt="Map" />
                                        </Side>
                                    </>
                                )}
                            </Transition>
                        </Page>
                    )}
                />
                <Transition
                    nodeRef={nodeRef}
                    in={snackbar !== ""}
                    addEndListener={(nodeRef, done) => {
                        nodeRef.addEventListener("transitionend", done, false);
                    }}
                >
                    {(state) => <Snackbar state={state}>{snackbar}</Snackbar>}
                </Transition>
            </>
        </Router>
    );
};

export default App;
