import React, { useEffect } from "react";
import styled, { css } from "styled-components";
import { Device } from "components/globalStyle";
import { Button, FormItem } from "components/components";

type StateProps = {
    readonly state?: string;
};

const Container = styled.main<StateProps>`
    width: 100%;
    padding: 20px 40px;
    display: grid;
    justify-items: center;
    color: var(--on-secondary);

    @media ${Device.laptop} {
        max-width: 40vw;
        padding: 20px 40px;
    }

    ${(props) =>
        props.state === "entering"
            ? css`
                  position: absolute;
                  visibility: hidden;
                  opacity: 0;
                  transform: scale(1.1);
                  transition: 0.15s ease-in;
              `
            : props.state === "exiting"
            ? css`
                  visibility: hidden;
                  opacity: 0;
                  transform: scale(0.9);
                  transition: 0.15s ease-in;
              `
            : css`
                  visibility: visible;
                  opacity: 1;
                  transform: initial;
                  transition: 0.3s ease-out;
              `};
`;

const Card = styled.form`
    position: relative;
    border-radius: 6px;
    background: var(--surface);
    color: var(--on-background);
    box-shadow: var(--shadow0);

    @media ${Device.laptop} {
    }
`;

const Send = styled(Button)`
    position: absolute;
    bottom: -24px;
    right: 20px;
    background: var(--secondary);
    color: var(--on-secondary);
`;

const Encuentranos = function ({ state, setSnackbar }) {
    useEffect(() => {}, []);

    const submit = (event) => {
        event.preventDefault();
        setSnackbar("Reparacion creada");
    };

    return (
        <>
            <Container state={state}>
                <p>
                    Encuéntranos en primera línea de playa y a dos cuadras del
                    centro de la ciudad • Donde estamos: muelle 1 Parador Cabo
                    frio. COSTANERA 278
                </p>
                <Card onSubmit={submit} noValidate>
                    <FormItem half>
                        <p>Nombre</p>
                        <input
                            type="text"
                            name="nombre"
                            placeholder="-"
                            autoComplete="off"
                            required
                        />
                    </FormItem>
                    <FormItem half>
                        <p>Email</p>
                        <input
                            type="email"
                            name="nombre"
                            placeholder="-"
                            autoComplete="off"
                            required
                        />
                    </FormItem>
                    <FormItem>
                        <p>Consulta</p>
                        <textarea
                            name="nombre"
                            placeholder="-"
                            autoComplete="off"
                            required
                        />
                    </FormItem>
                    <Send type="submit" icon>
                        <i className="material-icons md-dark md-24">send</i>
                    </Send>
                </Card>
            </Container>
        </>
    );
};

export default Encuentranos;
