import React, { useEffect } from "react";
import styled, { css } from "styled-components";
import { Device } from "components/globalStyle";
import img01 from "./img/1.jpg";
import img02 from "./img/2.jpg";
import img03 from "./img/3.jpg";
import img04 from "./img/4.jpg";
import img05 from "./img/5.jpg";

type StateProps = {
    readonly state?: string;
};

const Main = styled.section<StateProps>`
    width: 100%;
    max-height: 100vh;
    padding: 40px;
    display: grid;

    @media ${Device.laptop} {
        overflow-y: auto;
    }

    ${(props) =>
        props.state === "entered"
            ? css`
                  visibility: visible;
                  opacity: 1;
                  transform: scale(1);
                  transition: 0.3s ease-out;
              `
            : props.state === "entering"
            ? css`
                  visibility: hidden;
                  opacity: 0;
                  transform: scale(0.9);
                  transition: 0.3s ease-in;
              `
            : css`
                  visibility: hidden;
                  opacity: 0;
                  transition: 0.15s ease-in;
              `};
`;

const Card = styled.div`
    width: 100%;
    border-radius: 6px;
    background: var(--surface);
    box-shadow: var(--shadow0);
    display: grid;

    img {
        width: 100%;
    }

    @media ${Device.laptop} {
        grid-template-columns: 1fr 1fr;
    }
`;

const Galeria = function ({ state }) {
    useEffect(() => {}, []);

    return (
        <>
            <Main state={state}>
                <Card>
                    <img src={img01} alt="" />
                    <img src={img02} alt="" />
                    <img src={img03} alt="" />
                    <img src={img04} alt="" />
                    <img src={img05} alt="" />
                </Card>
            </Main>
        </>
    );
};

export default Galeria;
