import React, { useState, useEffect } from "react";
import {
    BrowserRouter as Router,
    Route,
    Switch,
    NavLink,
} from "react-router-dom";
import { TransitionGroup, Transition } from "react-transition-group";
import styled, { css, ThemeProvider } from "styled-components";
import { themeDark, themeLight } from "themes";
import GlobalStyle, { Device } from "components/globalStyle";
import Home from "views/Home/Home";
import Galeria from "views/Galeria/Galeria";
import Propuesta from "views/Propuesta/Propuesta";
import logo from "amares-l.png";
import Cercania from "views/Cercania/Cercania";
import Encuentranos from "views/Encuentranos/Encuentranos";
import Mapa from "views/Mapa/Mapa";

type StateProps = {
    readonly state?: string;
    readonly location?: string;
    readonly to?: string;
};

const Main = styled.div<StateProps>`
    width: 100vw;
    align-items: center;

    @media ${Device.laptop} {
        position: fixed;
        z-index: 1;
        top: 0;
        left: 0;
        height: 100vh;
        overflow-y: auto;
        overflow-x: hidden;
        display: grid;
        grid-template-rows: auto 1fr auto;
        max-width: ${(props) =>
            props.location === "/propuesta" ? "60vw" : "40vw"};
        transition: 0.3s ease-out;
    }
`;

const Side = styled.div<StateProps>`
    width: 100%;
    border-radius: 40px 40px 0 0;
    background: #29434999;
    backdrop-filter: blur(10px);
    box-shadow: var(--shadow0);
    overflow: hidden;

    @media ${Device.laptop} {
        position: fixed;
        top: 0;
        right: 0;
        height: 100vh;
        transition: 0.3s ease-out;
    }

    ${(props) =>
        props.location === "/propuesta"
            ? css`
                  color: var(--on-secondary);

                  @media ${Device.laptop} {
                      border-radius: 0;
                      max-width: 40%;
                  }
              `
            : css`
                  border: 4px solid var(--primary);

                  @media ${Device.laptop} {
                      max-width: 60%;
                      border-radius: 40px 0 0 40px;
                  }
              `};
`;

const Header = styled.div<StateProps>`
    width: 100%;
    padding: 40px;
    text-align: center;

    img {
        width: auto;
        max-width: 100%;
        max-height: 200px;
    }
`;

const Fabs = styled.div<StateProps>`
    position: sticky;
    z-index: 3;
    bottom: 0;
    width: 100%;
    padding: 40px 0;
    text-align: center;

    @media ${Device.laptop} {
        position: sticky;
    }
`;

const Fab = styled.div<StateProps>`
    display: inline-block;
    transition: 0.3s ease-out;

    ${(props) =>
        props.to === props.location
            ? css`
                  opacity: 0;
                  width: 0;
                  transition: 0.15s ease-in;
              `
            : css`
                  opacity: 1;
                  width: 200px;
              `};
`;

const Link = styled(NavLink)`
    padding: 7px 16px;
    margin: 8px;
    background: none;
    border-radius: 4px;
    border: 1px solid var(--secondary);
    box-shadow: none;
    font: 500 2rem/2.8rem var(--font-family);
    text-transform: uppercase;
    text-decoration: none;
    text-align: center;
    box-shadow: var(--shadow0);
    transition: 0.1s ease-in-out;

    &:hover {
        cursor: pointer;
        border: 1px solid var(--secondary-variant);
        color: var(--secondary-variant);
    }

    ${(props) =>
        props.to === "/encuentranos"
            ? css`
                  background: var(--secondary);
                  color: var(--primary);

                  &:hover {
                      background-color: var(--secondary-variant);
                      color: var(--primary);
                  }
              `
            : css`
                  color: var(--secondary);
              `};
`;

const Snackbar = styled.div<StateProps>`
    position: fixed;
    z-index: 100;
    left: 0;
    bottom: 0;
    width: 100%;
    min-height: 45px;
    background: var(--on-background);
    color: var(--on-secondary);
    padding: 12px 16px;
    border-radius: 5px;
    box-shadow: 0px 2px 3px rgba(0, 0, 0, 0.3);
    letter-spacing: 0.1px;
    letter-spacing: 0.01rem;
    font: 400 1.3rem/2.4rem var(--font-family);
    text-transform: inherit;
    visibility: hidden;
    transform: translateY(100%);
    transition: 0.25s ease-in;

    @media ${Device.laptop} {
        width: auto;
        min-width: 344px;
        margin: 28px;
    }

    ${(props) =>
        props.state === "entered" &&
        css`
            visibility: visible;
            transform: translateY(0%);
            transition: 0.3s ease-out;
        `};
`;

const App = function () {
    const nodeRef = React.useRef(null);
    const [snackbar, setSnackbar] = useState("");
    const [darkTheme, setDarkTheme] = useState(false);

    useEffect(() => {
        setDarkTheme(false);
    }, []);

    useEffect(() => {
        if (snackbar !== "") {
            setTimeout(function () {
                setSnackbar("");
            }, 5500);
        }
    }, [snackbar]);

    return (
        <Router>
            <ThemeProvider theme={darkTheme ? themeDark : themeLight}>
                <GlobalStyle />
            </ThemeProvider>
            <>
                <Route
                    render={({ location }) => (
                        <>
                            <Main location={location.pathname}>
                                <Header location={location.pathname}>
                                    <img src={logo} alt="Logo" />
                                </Header>
                                <TransitionGroup component={null}>
                                    <Transition
                                        key={location.key}
                                        timeout={300}
                                    >
                                        {(state) => (
                                            <Switch
                                                key={location.key}
                                                location={location}
                                            >
                                                <Route
                                                    exact
                                                    path="/"
                                                    render={() => (
                                                        <Home state={state} />
                                                    )}
                                                />
                                                <Route
                                                    path="/propuesta"
                                                    render={() => (
                                                        <Propuesta
                                                            state={state}
                                                        />
                                                    )}
                                                />
                                                <Route
                                                    exact
                                                    path="/encuentranos"
                                                    render={() => (
                                                        <Encuentranos
                                                            state={state}
                                                            setSnackbar={
                                                                setSnackbar
                                                            }
                                                        />
                                                    )}
                                                />
                                            </Switch>
                                        )}
                                    </Transition>
                                </TransitionGroup>
                                <Fabs>
                                    <Fab location={location.pathname} to="/">
                                        <Link exact to="/">
                                            Inicio
                                        </Link>
                                    </Fab>
                                    <Fab
                                        location={location.pathname}
                                        to="/propuesta"
                                    >
                                        <Link to="/propuesta">Propuesta</Link>
                                    </Fab>
                                    <Fab
                                        location={location.pathname}
                                        to="/encuentranos"
                                    >
                                        <Link to="/encuentranos">
                                            Encuentranos
                                        </Link>
                                    </Fab>
                                </Fabs>
                            </Main>
                            <Side location={location.pathname}>
                                <TransitionGroup component={null}>
                                    <Transition
                                        key={location.key}
                                        timeout={300}
                                    >
                                        {(state) => (
                                            <Switch
                                                key={location.key}
                                                location={location}
                                            >
                                                <Route
                                                    exact
                                                    path="/"
                                                    render={() => (
                                                        <Galeria
                                                            state={state}
                                                        />
                                                    )}
                                                />
                                                <Route
                                                    path="/propuesta"
                                                    render={() => (
                                                        <Cercania
                                                            state={state}
                                                        />
                                                    )}
                                                />
                                                <Route
                                                    path="/encuentranos"
                                                    render={() => (
                                                        <Mapa state={state} />
                                                    )}
                                                />
                                            </Switch>
                                        )}
                                    </Transition>
                                </TransitionGroup>
                            </Side>
                        </>
                    )}
                />
                <Transition
                    nodeRef={nodeRef}
                    in={snackbar !== ""}
                    addEndListener={(nodeRef, done) => {
                        nodeRef.addEventListener("transitionend", done, false);
                    }}
                >
                    {(state) => <Snackbar state={state}>{snackbar}</Snackbar>}
                </Transition>
            </>
        </Router>
    );
};

export default App;
