import React, { useEffect } from "react";
import styled, { css } from "styled-components";
import { Device } from "components/globalStyle";
import img01 from "./img/01.jpg";
import img02 from "./img/02.jpg";
import img03 from "./img/03.jpg";
import img04 from "./img/04.jpg";
import img05 from "./img/05.jpg";
import img06 from "./img/06.jpg";

type StateProps = {
    readonly state?: string;
};

const Main = styled.section<StateProps>`
    width: 100%;
    max-height: 100vh;
    padding: 40px 40px 40px 0;
    display: grid;

    ${(props) =>
        props.state === "entered"
            ? css`
                  visibility: visible;
                  opacity: 1;
                  transform: scale(1);
                  transition: 0.3s ease-out;
              `
            : props.state === "entering"
            ? css`
                  visibility: hidden;
                  opacity: 0;
                  transform: scale(0.9);
                  transition: 0.3s ease-in;
              `
            : css`
                  visibility: hidden;
                  opacity: 0;
                  transition: 0.15s ease-in;
              `};
`;

const Card = styled.div`
    width: 100%;
    border-radius: 6px;
    background: var(--surface);
    box-shadow: var(--shadow0);
    display: grid;

    img {
        width: 100%;
    }

    @media ${Device.laptop} {
        grid-template-columns: 1fr 1fr;
        overflow-y: auto;
    }
`;

type ImgProps = {
    readonly wide?: boolean;
};

const Img = styled.img<ImgProps>`
    ${(props) =>
        props.wide &&
        css`
            grid-column-end: span 2;
        `};
`;

const Galeria = function ({ state }) {
    useEffect(() => {}, []);

    return (
        <>
            <Main state={state}>
                <Card>
                    <Img src={img01} alt="" wide />
                    <Img src={img02} alt="" />
                    <Img src={img03} alt="" />
                    <Img src={img04} alt="" />
                    <Img src={img05} alt="" />
                    <Img src={img06} alt="" />
                </Card>
            </Main>
        </>
    );
};

export default Galeria;
