import React, { useEffect } from "react";
import styled, { css } from "styled-components";
import { Device } from "components/globalStyle";
import { NavLink } from "react-router-dom";

type StateProps = {
    readonly state?: string;
};

const Container = styled.main<StateProps>`
    width: 100%;
    padding: 40px;
    display: grid;
    justify-items: center;
    color: var(--on-secondary);

    @media ${Device.laptop} {
        max-width: 40vw;
        padding: 80px;
    }

    ${(props) =>
        props.state === "entering"
            ? css`
                  position: absolute;
                  visibility: hidden;
                  opacity: 0;
                  transform: scale(1.1);
                  transition: 0.15s ease-in;
              `
            : props.state === "exiting"
            ? css`
                  visibility: hidden;
                  opacity: 0;
                  transform: scale(0.9);
                  transition: 0.15s ease-in;
              `
            : css`
                  visibility: visible;
                  opacity: 1;
                  transform: initial;
                  transition: 0.3s ease-out;
              `};
`;

const Link = styled(NavLink)`
    padding: 7px 16px;
    margin: 4px;
    background: none;
    border-radius: 4px;
    border: 1px solid var(--secondary);
    box-shadow: none;
    letter-spacing: 0.1px;
    letter-spacing: 0.01rem;
    font: 500 1.4rem/2rem var(--font-family);
    text-transform: uppercase;
    text-decoration: none;
    text-align: center;
    color: var(--secondary);
    transition: 0.1s ease-in-out;

    &:hover {
        cursor: pointer;
        border: 1px solid var(--secondary-variant);
        color: var(--secondary-variant);
        box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.1);
    }

    i {
        padding: 12px 20px 12px 0;
    }
`;

const Home = function ({ state }) {
    useEffect(() => {}, []);

    return (
        <>
            <Container state={state}>
                <p>
                    Apart con vista al mar y balcones terrazas con parrilla
                    individual a gas. Equipado con todos los utensilios para
                    cocinar, heladera con freezer, calefacci√≥n central, cable y
                    wifi. Parrillas en patio, SUM .
                </p>
                <Link to="/propuesta">Propuesta</Link>
            </Container>
        </>
    );
};

export default Home;
