import React, { useEffect } from "react";
import styled, { css } from "styled-components";
import { Device } from "components/globalStyle";
import img01 from "./img/01.jpg";
import img02 from "./img/02.jpg";
import img03 from "./img/03.jpg";
import img04 from "./img/04.jpg";
import img05 from "./img/05.jpg";
import img06 from "./img/06.jpg";
import img07 from "./img/07.jpg";
import img08 from "./img/08.jpg";
import img09 from "./img/09.jpg";
import img10 from "./img/10.jpg";
import img11 from "./img/11.jpg";

type StateProps = {
    readonly state?: string;
};

const Card = styled.div<StateProps>`
    position: fixed;
    top: 0;
    right: 0;
    width: 100%;
    max-height: 100vh;
    border: 4px solid var(--primary);
    border-radius: 40px 40px 0 0;
    background: var(--primary);
    box-shadow: var(--shadow0);
    display: grid;
    gap: 4px;
    overflow: hidden;

    @media ${Device.laptop} {
        width: 60%;
        grid-column-start: 2;
        grid-row-start: 1;
        grid-row-end: span 2;
        max-height: 100vh;
        border-radius: 40px 0 0 40px;
        overflow-y: auto;
        grid-template-columns: 1fr 1fr;
    }

    ${(props) =>
        props.state === "entered"
            ? css`
                  visibility: visible;
                  opacity: 1;
                  transform: initial;
                  transition: 0.3s ease-out;
              `
            : props.state === "entering"
            ? css`
                  visibility: hidden;
                  opacity: 0;
                  transform: translateY(20%);
                  transition: 0.3s ease-in;

                  @media ${Device.laptop} {
                      transform: translateX(20%);
                  }
              `
            : props.state === "exiting"
            ? css`
                  visibility: hidden;
                  opacity: 0;
                  transform: initial;
                  transition: 0.3s ease-in;
              `
            : css`
                  visibility: hidden;
                  opacity: 0;
                  transition: 0.15s ease-in;
              `};
`;

type ImgProps = {
    readonly wide?: boolean;
    readonly tall?: boolean;
};

const Img = styled.img<ImgProps>`
    width: 100%;

    ${(props) =>
        props.wide &&
        css`
            grid-column-end: span 2;
        `};

    ${(props) =>
        props.tall &&
        css`
            height: 100%;
            grid-row-end: span 2;
        `};
`;

const Galeria = function ({ state }) {
    useEffect(() => {}, []);

    return (
        <>
            <Card state={state}>
                <Img src={img01} alt="" wide />
                <Img src={img02} alt="" />
                <Img src={img03} alt="" />
                <Img src={img04} alt="" />
                <Img src={img05} alt="" tall />
                <Img src={img06} alt="" />
                <Img src={img07} alt="" tall />
                <Img src={img08} alt="" />
                <Img src={img09} alt="" />
                <Img src={img10} alt="" />
                <Img src={img11} alt="" />
            </Card>
        </>
    );
};

export default Galeria;
