import React, { useEffect } from "react";
import styled, { css } from "styled-components";
import { Device } from "components/globalStyle";
import { NavLink } from "react-router-dom";
import Galeria from "views/Galeria/Galeria";

type StateProps = {
    readonly state?: string;
};

const Main = styled.main<StateProps>`
    width: 100%;
    max-width: 700px;
    padding: 40px;
    display: grid;
    justify-items: center;
    color: var(--on-secondary);

    @media ${Device.laptop} {
        padding: 80px;
    }

    ${(props) =>
        props.state === "entering" || props.state === "exiting"
            ? css`
                  visibility: hidden;
                  opacity: 0;
                  transform: scale(0.9);
                  transition: 0.15s ease-in;
              `
            : css`
                  visibility: visible;
                  opacity: 1;
                  transform: initial;
                  transition: 0.3s ease-out;
              `};
`;

const Side = styled.div<StateProps>`
    width: 100%;
    border: 4px solid var(--primary);
    border-radius: 40px 40px 0 0;
    background: var(--primary);
    box-shadow: var(--shadow0);
    display: grid;
    gap: 4px;
    overflow: hidden;

    @media ${Device.laptop} {
        position: fixed;
        top: 0;
        right: 0;
        max-height: 100vh;
        width: 60%;
        border-radius: 40px 0 0 40px;
        overflow-y: auto;
        grid-template-columns: 1fr 1fr;
    }

    ${(props) =>
        props.state === "entering" || props.state === "exiting"
            ? css`
                  visibility: hidden;
                  opacity: 0;
                  transform: translateY(20%);
                  transition: 0.15s ease-in;

                  @media ${Device.laptop} {
                      transform: translateX(20%);
                  }
              `
            : css`
                  visibility: visible;
                  opacity: 1;
                  transform: initial;
                  transition: 0.3s ease-out;
              `};
`;

const Link = styled(NavLink)`
    padding: 7px 16px;
    margin: 4px;
    background: none;
    border-radius: 4px;
    border: 1px solid var(--secondary);
    box-shadow: none;
    letter-spacing: 0.1px;
    letter-spacing: 0.01rem;
    font: 500 1.4rem/2rem var(--font-family);
    text-transform: uppercase;
    text-decoration: none;
    text-align: center;
    color: var(--secondary);
    transition: 0.1s ease-in-out;

    &:hover {
        cursor: pointer;
        border: 1px solid var(--secondary-variant);
        color: var(--secondary-variant);
        box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.1);
    }

    i {
        padding: 12px 20px 12px 0;
    }
`;

const Home = function ({ state }) {
    useEffect(() => {}, []);

    return (
        <>
            <Main state={state}>
                <p>
                    Apart con vista al mar y balcones terrazas con parrilla
                    individual a gas. Equipado con todos los utensilios para
                    cocinar, heladera con freezer, calefacci√≥n central, cable y
                    wifi. Parrillas en patio, SUM .
                </p>
                <Link exact to="/propuesta">
                    Propuesta
                </Link>
            </Main>
            <Side state={state}>
                <Galeria />
            </Side>
        </>
    );
};

export default Home;
