import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { TransitionGroup, Transition } from "react-transition-group";
import styled, { css, ThemeProvider } from "styled-components";
import { themeDark, themeLight } from "themes";
import GlobalStyle, { Device } from "components/globalStyle";
import { Button, FormItem } from "components/components";
import Home from "views/Home/Home";
import Propuesta from "views/Propuesta/Propuesta";
import map from "./map.png";
import logo from "amares-l.png";

const Page = styled.div`
    position: fixed;
    z-index: 1;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    padding-bottom: 94px;
    overflow-y: auto;
    overflow-x: hidden;
    display: grid;
    align-items: center;

    @media ${Device.laptop} {
        grid-template-columns: 2fr 3fr;
        padding-bottom: 0;
    }

    @media ${Device.desktop} {
    }
`;

type StateProps = {
    readonly state?: string;
    readonly location?: string;
};

const Header = styled.div<StateProps>`
    width: 100%;
    padding: 40px;
    text-align: center;
    transition: 0.5s ease-in-out;

    img {
        width: 100%;
        max-width: 500px;
    }

    @media ${Device.laptop} {
        max-width: ${(props) => (props.location === "/" ? "40%" : "100%")};
    }
`;

export const Fab = styled.div<StateProps>`
    position: fixed;
    z-index: 3;
    bottom: 0;
    width: 100%;
    padding: 40px;
    text-align: center;
    transition: 0.5s ease-in-out;

    ${Button} {
        padding: 7px 32px;
        border-radius: 100px;
        color: var(--primary);
        border: none;
        font-size: 2.4rem;
        line-height: 3.2rem;
    }

    @media ${Device.laptop} {
        max-width: ${(props) => (props.location === "/" ? "40%" : "100%")};
    }
`;

const Overlay = styled.div<StateProps>`
    position: fixed;
    top: 0;
    z-index: 2;
    inset: 0;
    width: 100vw;
    height: 100vh;
    background: rgba(228, 216, 198, 0.5);
    opacity: 0;
    visibility: hidden;
    transition: 0.3s ease-in-out;
    backdrop-filter: blur(10px);

    ${(props) =>
        props.state === "entered" &&
        css`
            opacity: 1;
            visibility: visible;
        `};
`;

const Side = styled.aside<StateProps>`
    position: fixed;
    z-index: 4;
    height: auto;
    max-height: 90%;
    overflow: auto;
    width: 100%;
    visibility: hidden;
    bottom: -100%;
    transition: 0.25s ease-in;
    background: var(--primary);
    background-image: url("amares2.jpg");
    background-size: cover;
    background-position: center;
    color: var(--on-secondary);
    clip-path: polygon(
        63% 1%,
        81% 1%,
        100% 0,
        100% 100%,
        0 100%,
        0 4%,
        13% 4%,
        30% 3%,
        53% 1%
    );
    display: grid;
    align-items: center;

    img {
        width: 100%;
    }

    > div {
        padding: 40px 40px 0 40px;
        grid-row-start: 1;
    }

    > div p {
        font-size: 15px;
        font-size: 1.5rem;
        line-height: 28px;
        line-height: 2.8rem;
        letter-spacing: 0.1px;
        letter-spacing: 0.01rem;
        font-weight: 400;
    }

    @media ${Device.laptop} {
        max-height: 80%;
        overflow: hidden;
        grid-template-columns: 3fr 1fr 2fr;
        grid-template-rows: 1fr 1fr;
        clip-path: polygon(
            52% 4%,
            67% 3%,
            85% 5%,
            100% 0,
            100% 100%,
            0 100%,
            0 19%,
            14% 16%,
            27% 10%,
            42% 8%
        );

        img {
            grid-column-start: 1;
            grid-column-end: span 2;
            grid-row-start: 1;
            grid-row-end: span 2;
        }
    }

    ${(props) =>
        props.state === "entered" &&
        css`
            visibility: visible;
            bottom: 0;
            transition: 0.3s ease-out;
        `};
`;

const Card = styled.form`
    margin: 20px;
    border-radius: 6px;
    background: var(--surface);
    color: var(--on-background);
    box-shadow: var(--shadow0);

    @media ${Device.laptop} {
        grid-column-start: 2;
        grid-column-end: span 2;
        grid-row-start: 2;
    }
`;

const Snackbar = styled.div<StateProps>`
    position: fixed;
    z-index: 100;
    left: 0;
    bottom: 0;
    width: 100%;
    min-height: 45px;
    background: var(--on-background);
    color: var(--on-secondary);
    padding: 12px 16px;
    border-radius: 5px;
    box-shadow: 0px 2px 3px rgba(0, 0, 0, 0.3);
    font-size: 13px;
    font-size: 1.3rem;
    line-height: 24px;
    line-height: 2.4rem;
    letter-spacing: 0.1px;
    letter-spacing: 0.01rem;
    font-weight: 400;
    text-transform: inherit;
    visibility: hidden;
    transform: translateY(100%);
    transition: 0.25s ease-in;

    @media ${Device.laptop} {
        width: auto;
        min-width: 344px;
        margin: 28px;
    }

    ${(props) =>
        props.state === "entered" &&
        css`
            visibility: visible;
            transform: translateY(0%);
            transition: 0.3s ease-out;
        `};
`;

const App = function () {
    const nodeRef = React.useRef(null);
    const [sideOpen, setSideOpen] = useState(false);
    const [snackbar, setSnackbar] = useState("");
    const [darkTheme, setDarkTheme] = useState(false);

    const submit = (event) => {
        event.preventDefault();
        setSnackbar("Reparacion creada");
    };

    useEffect(() => {
        setDarkTheme(false);
    }, []);

    useEffect(() => {
        if (snackbar !== "") {
            setTimeout(function () {
                setSnackbar("");
            }, 5500);
        }
    }, [snackbar]);

    return (
        <Router>
            <ThemeProvider theme={darkTheme ? themeDark : themeLight}>
                <GlobalStyle />
            </ThemeProvider>
            <>
                <Route
                    render={({ location }) => (
                        <Page>
                            <Header location={location.pathname}>
                                <img src={logo} alt="Logo" />
                            </Header>
                            <TransitionGroup component={null}>
                                <Transition key={location.key} timeout={300}>
                                    {(state) => (
                                        <Switch
                                            key={location.key}
                                            location={location}
                                        >
                                            <Route
                                                exact
                                                path="/"
                                                render={() => (
                                                    <>
                                                        <Home state={state} />
                                                    </>
                                                )}
                                            />
                                            <Route
                                                path="/propuesta"
                                                render={() => (
                                                    <Propuesta state={state} />
                                                )}
                                            />
                                        </Switch>
                                    )}
                                </Transition>
                            </TransitionGroup>
                            <Fab
                                location={location.pathname}
                                onClick={() => {
                                    setSideOpen(!sideOpen);
                                }}
                            >
                                <Button solid>Encuentranos</Button>
                            </Fab>
                            <Transition
                                nodeRef={nodeRef}
                                in={sideOpen}
                                addEndListener={(nodeRef, done) => {
                                    nodeRef.addEventListener(
                                        "transitionend",
                                        done,
                                        false
                                    );
                                }}
                            >
                                {(state) => (
                                    <>
                                        <Overlay
                                            state={state}
                                            onClick={() => {
                                                setSideOpen(false);
                                            }}
                                        />
                                        <Side state={state}>
                                            <img src={map} alt="Map" />
                                            <div>
                                                <img src={logo} alt="Logo" />
                                                <p>
                                                    Encuéntranos en primera
                                                    línea de playa y a dos
                                                    cuadras del centro de la
                                                    ciudad • Donde estamos:
                                                    muelle 1 Parador Cabo frio.
                                                </p>
                                            </div>
                                            <Card onSubmit={submit} noValidate>
                                                <FormItem half>
                                                    <p>Nombre</p>
                                                    <input
                                                        type="text"
                                                        name="nombre"
                                                        placeholder="-"
                                                        autoComplete="off"
                                                        required
                                                    />
                                                </FormItem>
                                                <FormItem half>
                                                    <p>Email</p>
                                                    <input
                                                        type="email"
                                                        name="nombre"
                                                        placeholder="-"
                                                        autoComplete="off"
                                                        required
                                                    />
                                                </FormItem>
                                                <FormItem>
                                                    <p>Consulta</p>
                                                    <textarea
                                                        name="nombre"
                                                        placeholder="-"
                                                        autoComplete="off"
                                                        required
                                                    />
                                                </FormItem>
                                            </Card>
                                        </Side>
                                    </>
                                )}
                            </Transition>
                        </Page>
                    )}
                />
                <Transition
                    nodeRef={nodeRef}
                    in={snackbar !== ""}
                    addEndListener={(nodeRef, done) => {
                        nodeRef.addEventListener("transitionend", done, false);
                    }}
                >
                    {(state) => <Snackbar state={state}>{snackbar}</Snackbar>}
                </Transition>
            </>
        </Router>
    );
};

export default App;
