import React, { useEffect } from "react";
import styled, { css } from "styled-components";
import { Device } from "components/globalStyle";
import { NavLink } from "react-router-dom";
import cerveza from "./img/cerveza.gif";
import santaclara from "./img/santaclara.jpg";
import marchiquita from "./img/marchiquita.jpg";
import mdp from "./img/mdp.gif";

type StateProps = {
    readonly state?: string;
};

const Main = styled.main<StateProps>`
    width: 100%;
    padding: 40px;
    display: grid;
    justify-items: center;
    align-items: start;
    gap: 80px;
    color: var(--on-secondary);

    @media ${Device.laptop} {
        padding: 40px 80px;
    }

    ${(props) =>
        props.state === "entered"
            ? css`
                  visibility: visible;
                  opacity: 1;
                  transform: initial;
                  transition: 0.3s ease-out;
              `
            : props.state === "entering"
            ? css`
                  visibility: hidden;
                  opacity: 0;
                  transform: scale(0.9);
                  transition: 0.3s ease-in;
              `
            : css`
                  visibility: hidden;
                  opacity: 0;
                  transition: 0.15s ease-in;
              `};
`;

const Section = styled.section`
    display: grid;
    gap: 40px;

    h2,
    h3,
    p {
        margin: 0;
    }
`;

const Side = styled(Section)<StateProps>`
    color: var(--on-secondary);
    overflow-y: auto;

    @media ${Device.laptop} {
        padding: 40px 80px;
        position: fixed;
        top: 0;
        right: 0;
        max-height: 100vh;
        width: 40%;
        background: #29434999;
        backdrop-filter: blur(10px);
        box-shadow: var(--shadow0);
    }

    ${(props) =>
        props.state === "entering" || props.state === "exiting"
            ? css`
                  visibility: hidden;
                  opacity: 0;
                  transform: translateY(20%);
                  transition: 0.15s ease-in;

                  @media ${Device.laptop} {
                      transform: translateX(20%);
                  }
              `
            : css`
                  visibility: visible;
                  opacity: 1;
                  transform: initial;
                  transition: 0.3s ease-out;
              `};
`;

const Article = styled.article`
    max-width: 700px;
    padding-bottom: 20px;
    border-bottom: 1px solid var(--primary-variant);
    display: grid;
    gap: 40px;

    img {
        width: 100%;
        max-width: 700px;
        border-radius: 6px;
        box-shadow: var(--shadow0);
        overflow: hidden;
    }
`;

const Link = styled(NavLink)`
    padding: 7px 16px;
    margin: 4px;
    background: none;
    border-radius: 4px;
    border: 1px solid var(--secondary);
    box-shadow: none;
    font-size: 14px;
    font-size: 1.4rem;
    line-height: 20px;
    line-height: 2rem;
    letter-spacing: 0.1px;
    letter-spacing: 0.01rem;
    font-weight: 500;
    text-transform: uppercase;
    text-decoration: none;
    text-align: center;
    color: var(--secondary);
    transition: 0.1s ease-in-out;

    &:hover {
        cursor: pointer;
        box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.1);
    }

    i {
        padding: 12px 20px 12px 0;
    }
`;

const Propuesta = function ({ state }) {
    useEffect(() => {}, []);

    return (
        <>
            <Main state={state}>
                <Section>
                    <h2>Santa Clara del Mar</h2>
                    <Article>
                        <p>
                            Un lugar apacible en las costas de la provincia de
                            Buenos Aires, donde el contacto con la naturaleza y
                            la ausencia de ruidos urbanos, hacen de esta
                            localidad, una singular y atractiva opción a la hora
                            de veranear en marcos de mar y serenidad. Pertenece
                            al Partido de Mar Chiquita, se encuentra ubicada a
                            390 Kms. de la Capital Federal y a 17 km de la
                            ciudad de Mar del Plata. La población actual es de
                            aproximadamente de 9.000 Habitantes.
                        </p>
                        <img src={santaclara} alt="" />
                        <p>
                            Playas divididas por espigones de piedra se
                            encuentran las siete playas de la ciudad. El acceso
                            es libre y cuenta con servicio de guardavidas
                            durante todo el verano. La geografía es bastante
                            particular ya que en sólo algo más de un kilómetro
                            el relieve cambia de playas cortas de arena compacta
                            con barrancos a playas extensas con médanos bajos.
                            Además de la diversidad y riqueza natural, la falta
                            de edificios altos en la línea de la costa hace que
                            la experiencia de vivir a pleno sol y aire puro se
                            prolongue mucho más.
                        </p>
                    </Article>
                    <Article>
                        <h3>Fiesta Cerveza Artesanal</h3>
                        <p>
                            Periodo de celebración: tercera semana de febrero.
                            Celebrado desde 1972. En esta fiesta el visitante
                            puede disfrutar de la degustación de diferentes
                            estilos de cervezas artesanales elaboradas en la
                            región. Cuenta con un patio de comidas, la elección
                            de la reina, shows musicales y feria de artesanos.
                            Organiza la Secretaria de Turismo del Municipio.
                            Santa Clara del Mar fue declarada Capital Marítima
                            de la Cerveza Artesanal por la Legislatura
                            provincial.
                        </p>
                        <img src={cerveza} alt="" />
                    </Article>
                    <Article>
                        <h3>Pesca Deportiva</h3>
                    </Article>
                    <Article>
                        <h3>Surf Kitesurt y Kayak faltan fotos</h3>
                    </Article>
                    <Article>
                        <h3>
                            Andar en bicicleta y juntar caracoles faltan fotos
                        </h3>
                    </Article>
                </Section>
                <Link exact to="/">
                    Propuesta
                </Link>
            </Main>
            <Side state={state}>
                <h2>Cercanía</h2>
                <Article>
                    <h3>Albufera de Mar Chiquita</h3>
                    <p>
                        Laguna litoral costera paralela al mar, con aguas
                        saladas y salabres, separada por una cadena de médanos y
                        unida al mar por un aboca. Reserva de biosfera, en 1996
                        declarada por la UNESCO.
                    </p>
                    <img src={marchiquita} alt="" />
                </Article>
                <Article>
                    <h3>Mar del Plata</h3>
                    <p>
                        Distancia a Santa Clara del Mar: 15 kmts por autopista
                        bordeando el mar. La ciudad es uno de los más
                        importantes centros turísticos de Argentina. La llamada
                        «Perla del Atlántico» o la «Biarritz argentina» -por la
                        marcada similitud con esa ciudad francesa- cuenta con
                        una variada oferta de temporada baja: turismo deportivo,
                        ecológico, aventura, pesca y eventos culturales son sólo
                        algunas de las alternativas que esta ciudad ofrece a sus
                        visitantes como también un interesante patrimonio
                        histórico y natural.
                    </p>
                    <img src={mdp} alt="" />
                </Article>
                <Article>
                    <h3>Mar de Cobo</h3>
                </Article>
            </Side>
        </>
    );
};

export default Propuesta;
