import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { TransitionGroup, Transition } from "react-transition-group";
import styled, { css, ThemeProvider } from "styled-components";
import { themeDark, themeLight } from "themes";
import GlobalStyle, { Device } from "components/globalStyle";
import { Fab } from "components/appBar";
import Home from "views/Home/Home";
import logo from "./amares-l.png";

const Page = styled.div`
    background-image: url("amares.jpg");
    background-size: cover;
    background-position: center;
    position: fixed;
    z-index: 1;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;

    @media ${Device.laptop} {
    }

    @media ${Device.desktop} {
    }
`;

const Header = styled.div`
    display: grid;
    grid-template-columns: 1fr auto;
    justify-items: center;
    align-items: center;
    justify-content: center;

    img {
        margin: 20px;
    }
`;
type StateProps = {
    readonly state?: string;
};

const Snackbar = styled.div<StateProps>`
    position: fixed;
    z-index: 100;
    left: 0;
    bottom: 0;
    width: 100%;
    min-height: 45px;
    background: var(--on-background);
    color: var(--on-secondary);
    padding: 12px 16px;
    border-radius: 5px;
    box-shadow: 0px 2px 3px rgba(0, 0, 0, 0.3);
    font-size: 13px;
    font-size: 1.3rem;
    line-height: 24px;
    line-height: 2.4rem;
    letter-spacing: 0.1px;
    letter-spacing: 0.01rem;
    font-weight: 400;
    text-transform: inherit;
    visibility: hidden;
    transform: translateY(100%);
    transition: 0.25s ease-in;

    @media ${Device.laptop} {
        width: auto;
        min-width: 344px;
        margin: 28px;
    }

    ${(props) =>
        props.state === "entered" &&
        css`
            visibility: visible;
            transform: translateY(0%);
            transition: 0.3s ease-out;
        `};
`;

const App = function () {
    const nodeRef = React.useRef(null);
    const [snackbar, setSnackbar] = useState("");
    const [darkTheme, setDarkTheme] = useState(true);

    useEffect(() => {
        setDarkTheme(true);
    }, []);

    useEffect(() => {
        if (snackbar !== "") {
            setTimeout(function () {
                setSnackbar("");
            }, 5500);
        }
    }, [snackbar]);

    return (
        <Router>
            <ThemeProvider theme={darkTheme ? themeDark : themeLight}>
                <GlobalStyle />
            </ThemeProvider>
            <>
                <Route
                    render={({ location }) => (
                        <Page>
                            <Header>
                                <img src={logo} alt="Logo" />
                                <Fab type="button" onClick={() => {}}>
                                    Encuentranos
                                </Fab>
                            </Header>
                            <TransitionGroup component={null}>
                                <Transition key={location.key} timeout={300}>
                                    {(state) => (
                                        <Switch
                                            key={location.key}
                                            location={location}
                                        >
                                            <Route
                                                exact
                                                path="/"
                                                render={() => (
                                                    <Home state={state} />
                                                )}
                                            />
                                            <Route
                                                path="/turnos"
                                                render={() => <div />}
                                            />
                                        </Switch>
                                    )}
                                </Transition>
                            </TransitionGroup>
                        </Page>
                    )}
                />
                <Transition
                    nodeRef={nodeRef}
                    in={snackbar !== ""}
                    addEndListener={(nodeRef, done) => {
                        nodeRef.addEventListener("transitionend", done, false);
                    }}
                >
                    {(state) => <Snackbar state={state}>{snackbar}</Snackbar>}
                </Transition>
            </>
        </Router>
    );
};

export default App;
