import React, { useEffect } from "react";
import styled, { css } from "styled-components";
import { Device } from "components/globalStyle";
import { NavLink } from "react-router-dom";
import { Button } from "components/components";
import logo from "amares-l.png";

type StateProps = {
    readonly state?: string;
};

const Main = styled.main<StateProps>`
    width: 100%;
    padding: 40px;
    display: grid;
    color: var(--on-secondary);

    p {
        font-size: 15px;
        font-size: 1.5rem;
        line-height: 28px;
        line-height: 2.8rem;
        letter-spacing: 0.1px;
        letter-spacing: 0.01rem;
        font-weight: 400;
    }

    @media ${Device.laptop} {
        padding: 80px;
    }

    ${(props) =>
        props.state === "entered"
            ? css`
                  visibility: visible;
                  opacity: 1;
                  transform: scale(1);
                  transition: 0.3s ease-out;
              `
            : props.state === "entering"
            ? css`
                  visibility: hidden;
                  opacity: 0;
                  transform: scale(0.9);
                  transition: 0.3s ease-in;
              `
            : css`
                  visibility: hidden;
                  opacity: 0;
                  transition: 0.15s ease-in;
              `};
`;

const Header = styled.div`
    padding: 20px;
    text-align: center;

    img {
        width: 100%;
        max-width: 500px;
    }
`;

const Link = styled(NavLink)`
    display: block;
    margin: 4px 0px;
    text-decoration: none;
    font-size: 14px;
    font-size: 1.4rem;
    line-height: 20px;
    line-height: 2rem;
    letter-spacing: 0.1px;
    letter-spacing: 0.01rem;
    font-weight: 500;
    color: var(--on-background-variant);
    transition: 0.1s ease-in-out;

    &:hover {
        cursor: pointer;
        box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.1);
    }

    i {
        padding: 12px 20px 12px 0;
    }

    &.${activeClassName} {
        color: var(--secondary);

        i {
            color: inherit;
        }
    }
`;

const Home = function ({ state }) {
    useEffect(() => {}, []);

    return (
        <>
            <Main state={state}>
                <Header>
                    <img src={logo} alt="Logo" />
                </Header>
                <p>
                    Apart con vista al mar y balcones terrazas con parrilla
                    individual a gas. Equipado con todos los utensilios para
                    cocinar, heladera con freezer, calefacci√≥n central, cable y
                    wifi. Parrillas en patio, SUM .
                </p>
                <Link exact to="/propuesta">
                    Propuesta
                </Link>
                <Button outline>Propuesta</Button>
            </Main>
        </>
    );
};

export default Home;
