import React, { useEffect } from "react";
import styled, { css } from "styled-components";
import img01 from "./img/1.jpg";

type StateProps = {
    readonly state?: string;
};

const Main = styled.section<StateProps>`
    width: 100%;
    max-height: 100vh;
    padding: 40px;
    display: grid;
    overflow-y: auto;

    ${(props) =>
        props.state === "entered"
            ? css`
                  visibility: visible;
                  opacity: 1;
                  transform: scale(1);
                  transition: 0.3s ease-out;
              `
            : props.state === "entering"
            ? css`
                  visibility: hidden;
                  opacity: 0;
                  transform: scale(0.9);
                  transition: 0.3s ease-in;
              `
            : css`
                  visibility: hidden;
                  opacity: 0;
                  transition: 0.15s ease-in;
              `};
`;

const Card = styled.div`
    width: 100%;
    max-height: 100%;
    border-radius: 6px;
    background: var(--surface);
    box-shadow: var(--shadow0);
    display: grid;
    overflow-y: auto;

    img {
        width: 100%;
    }
`;

const Galeria = function ({ state }) {
    useEffect(() => {}, []);

    return (
        <>
            <Main state={state}>
                <Card>
                    <img src={img01} alt="" />
                    <img src={img01} alt="" />
                    <img src={img01} alt="" />
                    <img src={img01} alt="" />
                    <img src={img01} alt="" />
                    <img src={img01} alt="" />
                </Card>
            </Main>
        </>
    );
};

export default Galeria;
