import React, { useEffect } from "react";
import styled, { css } from "styled-components";
import { Device } from "components/globalStyle";
import logo from "amares-l.png";

type StateProps = {
    readonly state?: string;
};

const Main = styled.main<StateProps>`
    width: 100%;
    padding: 40px;
    display: grid;
    color: var(--on-secondary);

    p {
        font-size: 15px;
        font-size: 1.5rem;
        line-height: 28px;
        line-height: 2.8rem;
        letter-spacing: 0.1px;
        letter-spacing: 0.01rem;
        font-weight: 400;
    }

    @media ${Device.laptop} {
        padding: 80px;
    }

    ${(props) =>
        props.state === "entered"
            ? css`
                  visibility: visible;
                  opacity: 1;
                  transform: scale(1);
                  transition: 0.3s ease-out;
              `
            : props.state === "entering"
            ? css`
                  visibility: hidden;
                  opacity: 0;
                  transform: scale(0.9);
                  transition: 0.3s ease-in;
              `
            : css`
                  visibility: hidden;
                  opacity: 0;
                  transition: 0.15s ease-in;
              `};
`;

const Header = styled.div`
    padding: 20px;
    text-align: center;

    img {
        width: 100%;
        max-width: 500px;
    }
`;

const Home = function ({ state }) {
    useEffect(() => {}, []);

    return (
        <>
            <Main state={state}>
                <Header>
                    <img src={logo} alt="Logo" />
                </Header>
                <p>
                    Apart con vista al mar y balcones terrazas con parrilla
                    individual a gas. Equipado con todos los utensilios para
                    cocinar, heladera con freezer, calefacci√≥n central, cable y
                    wifi. Parrillas en patio, SUM .
                </p>
            </Main>
        </>
    );
};

export default Home;
