import React, { useState, useEffect } from "react";
import styled, { css } from "styled-components";
import { Device } from "components/globalStyle";
import { Button } from "components/components";
import { AppBar, Left, Fab, Right } from "components/appBar";

type StateProps = {
    readonly state?: string;
};

const Main = styled.main<StateProps>`
    position: absolute;
    top: 0;
    width: 100%;
    max-height: 100%;
    grid-column-start: 2;
    display: grid;
    overflow-y: auto;

    ${(props) =>
        props.state === "entered"
            ? css`
                  visibility: visible;
                  opacity: 1;
                  transform: scale(1);
                  transition: 0.3s ease-out;
              `
            : props.state === "entering"
            ? css`
                  visibility: hidden;
                  opacity: 0;
                  transform: scale(0.9);
                  transition: 0.3s ease-in;
              `
            : css`
                  visibility: hidden;
                  opacity: 0;
                  transition: 0.15s ease-in;
              `};
`;

const Card = styled.section`
    padding-top: 16px;
    width: 100%;
    max-width: 600px;

    @media ${Device.laptop} {
    }

    h1 {
        text-align: center;
        margin: 0;
        padding: 12px 20px;
        font-size: 24px;
        font-size: 2.4rem;
        line-height: 32px;
        line-height: 3.2rem;
        letter-spacing: 0px;
        letter-spacing: 0rem;
        font-weight: 300;
    }
`;

type EventProps = {
    readonly row: number;
    readonly day?: boolean;
    readonly current?: boolean;
};

const Mes = styled.div`
    padding: 12px 20px;
    text-transform: capitalize;
    font-size: 15px;
    font-size: 1.5rem;
    line-height: 28px;
    line-height: 2.8rem;
    letter-spacing: 0.1px;
    letter-spacing: 0.01rem;
    font-weight: 400;
`;

const Turno = styled.div<EventProps>`
    display: grid;
    align-items: center;
    grid-template-columns: auto 1fr;

    ${(props) =>
        props.day &&
        css`
            ${Dia} {
                opacity: 1;
                visibility: visible;
            }
        `};

    p {
        margin: 0;
        font-size: 13px;
        font-size: 1.3rem;
        line-height: 24px;
        line-height: 2.4rem;
        letter-spacing: 0.1px;
        letter-spacing: 0.01rem;
        font-weight: 400;
    }

    span {
        color: var(--on-background-variant);
        font-size: 13px;
        font-size: 1.3rem;
        line-height: 20px;
        line-height: 2rem;
        letter-spacing: 0.1px;
        letter-spacing: 0.01rem;
        font-weight: 300;
    }
`;

const Dia = styled.div`
    padding: 12px 20px;
    opacity: 0;
    text-transform: uppercase;

    span {
        font-size: 13px;
        font-size: 1.3rem;
        line-height: 20px;
        line-height: 2rem;
        letter-spacing: 0.1px;
        letter-spacing: 0.01rem;
        font-weight: 300;
    }

    p {
        margin: 0;
        font-size: 20px;
        font-size: 2rem;
        line-height: 28px;
        line-height: 2.8rem;
        letter-spacing: 0.05px;
        letter-spacing: 0.005rem;
        font-weight: 400;
    }
`;

const Box = styled.div`
    display: grid;
    align-items: center;
    grid-template-columns: 1fr auto;
    border-bottom: 1px solid var(--on-background-disabled);
`;

const Turnos = function ({ menuOpen, setMenuOpen, state }) {
    const [sideOpen, setSideOpen] = useState(false);
    const [newOpen, setNewOpen] = useState(false);
    const [turnos, setTurnos] = useState({
        total: 0,
        limit: 0,
        skip: 0,
        data: [
            {
                id: 0,
                fecha: "",
                motivo: "",
                createdAt: "",
                updatedAt: "",
                modeloId: 0,
            },
        ],
    });

    useEffect(() => {
        setTurnos({
            total: 0,
            limit: 0,
            skip: 0,
            data: [
                {
                    id: 356,
                    fecha: "2020-08-25T23:59:59.000Z",
                    motivo: "Parrilla",
                    createdAt: "2020-08-20T13:12:55.000Z",
                    updatedAt: "2020-08-20T13:12:55.728Z",
                    modeloId: 157,
                },
                {
                    id: 357,
                    fecha: "2020-08-25T23:59:59.000Z",
                    motivo: "Frenos y limpieza de multiple admicion ",
                    createdAt: "2020-08-20T14:47:06.000Z",
                    updatedAt: "2020-08-20T14:47:06.886Z",
                    modeloId: 51,
                },
                {
                    id: 358,
                    fecha: "2020-08-26T23:59:59.000Z",
                    motivo: "Serv",
                    createdAt: "2020-08-20T15:39:07.000Z",
                    updatedAt: "2020-08-20T15:39:08.058Z",
                    modeloId: 114,
                },
                {
                    id: 363,
                    fecha: "2020-08-26T23:59:59.000Z",
                    motivo: "Consumo de agua",
                    createdAt: "2020-08-24T14:13:47.000Z",
                    updatedAt: "2020-08-24T14:13:47.575Z",
                    modeloId: 70,
                },
                {
                    id: 359,
                    fecha: "2020-08-27T23:59:59.000Z",
                    motivo: "Guardaplast nati",
                    createdAt: "2020-08-20T19:49:37.000Z",
                    updatedAt: "2020-08-20T19:49:38.018Z",
                    modeloId: 41,
                },
                {
                    id: 361,
                    fecha: "2020-08-27T23:59:59.000Z",
                    motivo: "Serv y bateria",
                    createdAt: "2020-08-24T12:06:34.000Z",
                    updatedAt: "2020-08-24T12:06:35.150Z",
                    modeloId: 25,
                },
                {
                    id: 362,
                    fecha: "2020-08-27T23:59:59.000Z",
                    motivo: "Controlar falla ",
                    createdAt: "2020-08-24T13:23:24.000Z",
                    updatedAt: "2020-08-24T13:23:25.487Z",
                    modeloId: 70,
                },
                {
                    id: 360,
                    fecha: "2020-08-31T23:59:59.000Z",
                    motivo: "Semieje",
                    createdAt: "2020-08-21T16:00:56.000Z",
                    updatedAt: "2020-08-21T16:00:57.671Z",
                    modeloId: 14,
                },
                {
                    id: 364,
                    fecha: "2020-09-01T23:59:59.000Z",
                    motivo: "Perd aceit",
                    createdAt: "2020-08-25T19:05:35.000Z",
                    updatedAt: "2020-08-25T19:05:36.169Z",
                    modeloId: 18,
                },
            ],
        });
    }, []);

    return (
        <>
            <Main state={state}>
                <Card>
                    <h1>Turnos</h1>
                    {turnos.data.length ? (
                        turnos.data.map((aTurno, index, array) => (
                            <>
                                {index === 0 ||
                                aTurno.fecha.substring(0, 8) !==
                                    array[index - 1].fecha.substring(0, 8) ? (
                                    <Mes>
                                        {new Date(
                                            aTurno.fecha
                                        ).toLocaleDateString("default", {
                                            month: "long",
                                        })}
                                    </Mes>
                                ) : undefined}
                                <Turno
                                    key={aTurno.id}
                                    row={index + 1}
                                    day={
                                        index === 0 ||
                                        aTurno.fecha.substring(0, 10) !==
                                            array[index - 1].fecha.substring(
                                                0,
                                                10
                                            )
                                            ? true
                                            : false
                                    }
                                >
                                    <Dia>
                                        <span>
                                            {new Date(
                                                aTurno.fecha
                                            ).toLocaleDateString("default", {
                                                weekday: "short",
                                            })}
                                        </span>
                                        <p>{aTurno.fecha.substring(8, 10)}</p>
                                    </Dia>
                                    <Box>
                                        <div>
                                            <p>{aTurno.motivo}</p>
                                            <span>
                                                {matchModelo(aTurno.modeloId)}
                                            </span>
                                        </div>
                                        <Button
                                            icon
                                            type="button"
                                            onClick={() => {}}
                                        >
                                            <i className="material-icons md-18">
                                                delete_outline
                                            </i>
                                        </Button>
                                    </Box>
                                </Turno>
                            </>
                        ))
                    ) : (
                        <div>
                            <i className="material-icons md-inactive md-48">
                                event
                            </i>
                            <h6>No hay turnos agendados</h6>
                        </div>
                    )}
                </Card>
            </Main>
            <AppBar>
                <Left>
                    <Button
                        icon
                        type="button"
                        onClick={() => {
                            setMenuOpen(!menuOpen);
                        }}
                    >
                        <i className="material-icons md-24">menu</i>
                    </Button>
                </Left>
                <Fab
                    type="submit"
                    onClick={() => {
                        setNewOpen(!newOpen);
                    }}
                    state={state}
                >
                    <i className="material-icons md-24">add</i>
                </Fab>
                <Right>
                    {sideOpen ? (
                        <Button
                            icon
                            type="button"
                            onClick={() => {
                                setSideOpen(!sideOpen);
                            }}
                        >
                            <i className="material-icons md-24">
                                calendar_view_day
                            </i>
                        </Button>
                    ) : (
                        <Button
                            icon
                            type="button"
                            onClick={() => {
                                setSideOpen(!sideOpen);
                            }}
                        >
                            <i className="material-icons md-24">history</i>
                        </Button>
                    )}
                </Right>
            </AppBar>
        </>
    );
};

export default Turnos;
